// Løsning i JSX for å sikre at både article-sidebar og ai-sidebar alltid vises på desktop og ved navigasjon

import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import { FiMessageCircle, FiList, FiSearch, FiCpu } from "react-icons/fi";
import "../assets/styles/ArticleSection.css";

function ArticleSection() {
  // Tilstander for visning av sidebars og hvilken visning som er valgt i article-sidebar
  const [sidebarHidden, setSidebarHidden] = useState(false);
  const [aiSidebarHidden, setAiSidebarHidden] = useState(false);
  const [sidebarView, setSidebarView] = useState("articles");
  const [isDesktop, setIsDesktop] = useState(window.innerWidth >= 1024);

  const location = useLocation();

  // Effekt for å oppdatere layout basert på skjermstørrelse
  useEffect(() => {
    const handleResize = () => {
      const isNowDesktop = window.innerWidth >= 1024;
      setIsDesktop(isNowDesktop);

      // Alltid vis sidebars i desktop-visning
      if (isNowDesktop) {
        setSidebarHidden(false);
        setAiSidebarHidden(false);
      }
    };

    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Effekt for å sikre at sidebars vises når URL inneholder søk eller endres
  useEffect(() => {
    const searchParams = new URLSearchParams(location.search);
    const hasQuery = searchParams.has("query");

    if (isDesktop || hasQuery) {
      setSidebarHidden(false);
      setAiSidebarHidden(false);
    }
  }, [location, isDesktop]);

  // Toggle for article-sidebar (kun aktiv på mobil og tablet)
  const toggleSidebar = () => {
    if (!isDesktop) {
      setSidebarHidden(!sidebarHidden);
    }
  };

  // Toggle for ai-sidebar (kun aktiv på mobil og tablet)
  const toggleAiSidebar = () => {
    if (!isDesktop) {
      setAiSidebarHidden(!aiSidebarHidden);
    }
  };

  // Åpner article-sidebar med valgt innhold (articles eller chat)
  const openSidebarWithView = (view) => {
    setSidebarView(view);
    if (sidebarHidden && !isDesktop) setSidebarHidden(false);
  };

  return (
    <section className="article-section">
      {/* ARTICLE SIDEBAR - vises alltid på desktop, og kan toggles på mindre skjermer */}
      {(!sidebarHidden || isDesktop) && (
        <div className="article-sidebar">
          {/* Lukkeknapp vises kun på mobil og tablet */}
          {!isDesktop && (
            <button onClick={toggleSidebar} className="close-article-sidebar-mobile">
              Close ✖
            </button>
          )}

          {/* Viser liste over artikler hvis valgt visning er "articles" */}
          {sidebarView === "articles" && (
            <ul className="results-list">
              <li className="result-item">
                <h4>Article Title 1</h4>
                <p><strong>Author:</strong> Alice</p>
              </li>
              <li className="result-item">
                <h4>Article Title 2</h4>
                <p><strong>Author:</strong> Bob</p>
              </li>
            </ul>
          )}

          {/* Viser chat-innhold hvis valgt visning er "chat" */}
          {sidebarView === "chat" && (
            <div className="chat-container">
              <h3>Chat about this article</h3>
              <p>This is where the AI chat will appear.</p>
            </div>
          )}
        </div>
      )}

      {/* AI SIDEBAR - vises alltid på desktop, kan toggles på mobil og tablet */}
      {(!aiSidebarHidden || isDesktop) && (
        <div className="ai-sidebar">
          {/* Lukkeknapp kun synlig på mobil og tablet */}
          {!isDesktop && (
            <button onClick={toggleAiSidebar} className="close-article-sidebar-mobile">
              Close ✖
            </button>
          )}
          <div className="chat-container">
            <h3>AI Assistant</h3>
            <p>This is where AI-related content appears.</p>
          </div>
        </div>
      )}

      {/* HOVEDINNHOLD */}
      <div className="article-content">
        {/* Toppmeny med knapper for å kontrollere sidebar-visninger */}
        <div className="article-topbar">
          <button onClick={() => openSidebarWithView("articles")}>
            <FiList /> Articles
          </button>
          <button onClick={() => openSidebarWithView("chat")}>
            <FiMessageCircle /> Ask AI
          </button>
          <button>
            <FiSearch /> Article summary
          </button>
          {/* Knapp for å toggle AI-sidebar, kun synlig på mobil og tablet */}
          {!isDesktop && (
            <button onClick={toggleAiSidebar}>
              <FiCpu /> Toggle AI Sidebar
            </button>
          )}
        </div>

        {/* Innholdsområde for valgt artikkel */}
        <div className="article-body">
          <h2>Selected Article</h2>
          <p><strong>Title:</strong> Example Title</p>
          <p><strong>Author(s):</strong> Example Author</p>
          <p><strong>Abstract:</strong> Full abstract text here...</p>
        </div>
      </div>
    </section>
  );
}

export default ArticleSection;
